import React from 'react';
import styled from 'styled-components';

const ProjectsSection = styled.section`
  padding: 60px 20px;
  background: #1a1a1a;
  color: #fff;
`;

const Title = styled.h2`
  font-family: 'Orbitron', sans-serif;
  font-size: 2.5rem;
  color: #00ffcc;
  text-align: center;
`;

const ProjectsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 20px;
  margin-top: 40px;
`;

const ProjectCard = styled.div`
  background: #2a2a2a;
  padding: 20px;
  border-radius: 10px;
  transition: transform 0.3s;

  &:hover {
    transform: scale(1.05);
  }
`;

const ProjectTitle = styled.h3`
  font-family: 'Roboto', sans-serif;
  font-size: 1.5rem;
  color: #00ffcc;
`;

const ProjectDescription = styled.p`
  font-family: 'Roboto', sans-serif;
  font-size: 1rem;
  color: #bbb;
  margin-top: 10px;
`;

const Link = styled.a`
  color: #00ffcc;
  text-decoration: none;
  margin-left: 5px;

  &:hover {
    text-decoration: underline;
  }
`;

const Projects = () => {
  return (
    <ProjectsSection id="projects">
      <Title>Proyectos</Title>
      <ProjectsGrid>
        <ProjectCard>
          <ProjectTitle>Hotel Golden Dream</ProjectTitle>
          <ProjectDescription>
            Desarrollo de una página web completa para un hotel, utilizando PHP como lenguaje principal. El sitio está diseñado para ser intuitivo, visualmente atractivo y funcional. Puedes ver más detalles del proyecto en <Link href="https://github.com/chicho377/Hotel-Golden-Dream" target="_blank">GitHub</Link>.
          </ProjectDescription>
        </ProjectCard>
        <ProjectCard>
          <ProjectTitle>To Do List</ProjectTitle>
          <ProjectDescription>
            Aplicación web para gestionar tareas diarias. Permite agregar, editar y eliminar tareas, y está diseñada para ser intuitiva y eficiente. Puedes ver más detalles del proyecto en <Link href="https://github.com/chicho377/to-do-list-app" target="_blank">GitHub</Link>.
          </ProjectDescription>
        </ProjectCard>
        <ProjectCard>
          <ProjectTitle>Gestión de Nómina de Empleados</ProjectTitle>
          <ProjectDescription>
            Programa en C++ que calcula salarios netos con base en ingresos, bonificaciones y deducciones. Ideal para empresas que necesitan una herramienta eficiente para gestionar la nómina. Puedes ver más detalles del proyecto en <Link href="https://github.com/chicho377/Gestion-de-Nomina-de-Empleados" target="_blank">GitHub</Link>.
          </ProjectDescription>
        </ProjectCard>
      </ProjectsGrid>
    </ProjectsSection>
  );
};

export default Projects;
