import React from 'react';
import styled from 'styled-components';

const ProjectsSection = styled.section`
  padding: 80px 20px;
  background: linear-gradient(135deg, #262626 0%, #1a1a1a 100%);
  color: #fff;
`;

const Title = styled.h2`
  font-family: 'Orbitron', sans-serif;
  font-size: 3rem;
  color: #00ffcc;
  text-align: center;
  margin-bottom: 50px;
`;

const ProjectsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 30px;
  justify-items: center;
`;

const ProjectCard = styled.div`
  background: #2a2a2a;
  padding: 20px;
  border-radius: 10px;
  transition: transform 0.3s, box-shadow 0.3s;
  cursor: pointer;
  box-shadow: 0px 8px 15px rgba(0, 0, 0, 0.1);

  &:hover {
    transform: scale(1.03);
    box-shadow: 0px 12px 20px rgba(0, 0, 0, 0.2);
  }
`;

const ProjectTitle = styled.h3`
  font-family: 'Roboto', sans-serif;
  font-size: 1.8rem;
  color: #00ffcc;
  margin-bottom: 15px;
`;

const ProjectDescription = styled.p`
  font-family: 'Roboto', sans-serif;
  font-size: 1.1rem;
  color: #ccc;
  line-height: 1.4;
`;

const Link = styled.a`
  color: #00ffcc;
  text-decoration: none;
  &:hover {
    text-decoration: underline;
  }
`;

const Projects = () => {
  return (
    <ProjectsSection id="projects">
      <Title>My Projects</Title>
      <ProjectsGrid>
        <ProjectCard>
          <ProjectTitle>Hotel Golden Dream</ProjectTitle>
          <ProjectDescription>
            Desarrollo de una página web completa para un hotel utilizando PHP como lenguaje principal. Proporciona una experiencia de usuario óptima, permitiendo a los visitantes obtener información detallada, explorar habitaciones disponibles, realizar reservas en línea y contactar con el hotel. Diseñado para ser intuitivo, visualmente atractivo y funcional. Detalles en <Link href="https://github.com/chicho377/Hotel-Golden-Dream" target="_blank">GitHub</Link>.
          </ProjectDescription>
        </ProjectCard>
        <ProjectCard>
          <ProjectTitle>To Do List App</ProjectTitle>
          <ProjectDescription>
            Aplicación web para gestionar tareas diarias, añadir subtareas, editar y eliminar tareas existentes. Interfaz de usuario intuitiva y en desarrollo activo. Detalles en <Link href="https://github.com/chicho377/to-do-list-app" target="_blank">GitHub</Link>.
          </ProjectDescription>
        </ProjectCard>
        <ProjectCard>
          <ProjectTitle>Gestor de Nóminas</ProjectTitle>
          <ProjectDescription>
            Programa en C++ para calcular salarios netos, ideal para empresas que gestionan nóminas de empleados. Interfaz de usuario intuitiva y en desarrollo activo. Detalles en <Link href="https://github.com/chicho377/Gestion-de-Nomina-de-Empleados" target="_blank">GitHub</Link>.
          </ProjectDescription>
        </ProjectCard>
      </ProjectsGrid>
    </ProjectsSection>
  );
};

export default Projects;
