import React from 'react';
import styled from 'styled-components';

const ProjectsSection = styled.section`
  padding: 60px 20px;
  background: #1a1a1a;
  color: #fff;
`;

const Title = styled.h2`
  font-family: 'Orbitron', sans-serif;
  font-size: 2.5rem;
  color: #00ffcc;
  text-align: center;
  margin-bottom: 40px;
`;

const ProjectsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 20px;
`;

const ProjectCard = styled.div`
  background: #2a2a2a;
  padding: 20px;
  border-radius: 10px;
  transition: transform 0.3s;
  cursor: pointer;

  &:hover {
    transform: scale(1.05);
  }
`;

const ProjectTitle = styled.h3`
  font-family: 'Roboto', sans-serif;
  font-size: 1.5rem;
  color: #00ffcc;
  margin-bottom: 10px;
`;

const ProjectDescription = styled.p`
  font-family: 'Roboto', sans-serif;
  font-size: 1rem;
  color: #bbb;
`;

const Link = styled.a`
  color: #00ffcc;
  text-decoration: none;
  &:hover {
    text-decoration: underline;
  }
`;

const Projects = () => {
  return (
    <ProjectsSection id="projects">
      <Title>Projects</Title>
      <ProjectsGrid>
        <ProjectCard>
          <ProjectTitle>Hotel Golden Dream</ProjectTitle>
          <ProjectDescription>
            El proyecto consiste en el desarrollo de una página web completa para un hotel, utilizando PHP como lenguaje de programación principal. El objetivo es proporcionar a los visitantes una experiencia de usuario óptima, permitiéndoles obtener información detallada sobre el hotel, explorar las habitaciones disponibles, realizar reservas en línea y ponerse en contacto fácilmente con el establecimiento. El sitio está diseñado para ser intuitivo, visualmente atractivo y funcional, asegurando que se adapte a diferentes dispositivos y resoluciones de pantalla. Puedes ver más detalles del proyecto en <Link href="https://github.com/chicho377/Hotel-Golden-Dream" target="_blank">GitHub</Link>.
          </ProjectDescription>
        </ProjectCard>
        <ProjectCard>
          <ProjectTitle>To Do List</ProjectTitle>
          <ProjectDescription>
            Esta aplicación web permite a los usuarios gestionar sus tareas diarias de manera efectiva. Puedes agregar nuevas tareas especificando el título, fecha de vencimiento, hora, prioridad y categoría. Además, puedes añadir subtareas, editar y eliminar tareas existentes según sea necesario. Actualmente en desarrollo y cuenta con una interfaz de usuario intuitiva. Ver más detalles en <Link href="https://github.com/chicho377/to-do-list-app" target="_blank">GitHub</Link>.
          </ProjectDescription>
        </ProjectCard>
        <ProjectCard>
          <ProjectTitle>Gestor de nóminas</ProjectTitle>
          <ProjectDescription>
            Programa hecho en C++ que calcula salarios netos basados en ingresos ordinarios, bonificaciones y deducciones de la Caja Costarricense de Seguro Social (CCSS). Ideal para empresas que necesitan una herramienta sencilla y eficiente para gestionar la nómina de sus empleados. Actualmente en desarrollo y cuenta con una interfaz de usuario intuitiva. Ver más detalles en <Link href="https://github.com/chicho377/Gestion-de-Nomina-de-Empleados" target="_blank">GitHub</Link>.
          </ProjectDescription>
        </ProjectCard>
      </ProjectsGrid>
    </ProjectsSection>
  );
};

export default Projects;
