import React from 'react';
import styled from 'styled-components';
import { useForm } from 'react-hook-form';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const PortfolioSection = styled.section`
  padding: 60px 20px;
  background: #111;
  color: #fff;
  text-align: center;
`;

const Title = styled.h2`
  font-family: 'Orbitron', sans-serif;
  font-size: 2.5rem;
  color: #00ffcc;
`;

const ContactContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 20px;
  flex-wrap: wrap;
  margin: 40px auto;
  max-width: 800px;
`;

const ContactCard = styled.div`
  background: #1a1a1a;
  border-radius: 10px;
  padding: 20px;
  text-align: left;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.1);
  width: calc(50% - 20px);
  max-width: 300px;
  margin: 10px;
  transition: transform 0.3s ease;

  &:hover {
    transform: translateY(-5px);
  }
`;

const ContactTitle = styled.h3`
  color: #00ffcc;
  font-size: 1.5rem;
`;

const ContactText = styled.p`
  color: #ddd;
`;

const SubmitButton = styled.button`
  padding: 10px 20px;
  font-size: 1.2rem;
  color: #fff;
  background: #00ffcc;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: background 0.3s ease;

  &:hover {
    background: #00bfa5;
  }
`;

const Contacts = () => {
  const { register, handleSubmit, reset, formState: { errors } } = useForm();

  const onSubmit = (data) => {
    // Simulación de envío de mensaje
    setTimeout(() => {
      toast.success('Message sent successfully!', {
        position: 'top-right',
        autoClose: 3000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });
      reset(); // Limpiar el formulario después de enviar
    }, 1000);
  };

  return (
    <PortfolioSection id="portfolio">
      <Title>My Portfolio</Title>
      <ContactContainer>
        <ContactCard>
          <ContactTitle>Web Development</ContactTitle>
          <ContactText>
            Looking for a stunning website? Let's discuss your project and bring
            it to life!
          </ContactText>
          <SubmitButton>Contact me</SubmitButton>
        </ContactCard>
        <ContactCard>
          <ContactTitle>Graphic Design</ContactTitle>
          <ContactText>
            Need eye-catching graphics? I can create designs that stand out.
          </ContactText>
          <SubmitButton>Contact me</SubmitButton>
        </ContactCard>
        <ContactCard>
          <ContactTitle>Consulting</ContactTitle>
          <ContactText>
            Want to optimize your business? Let's explore strategies for growth.
          </ContactText>
          <SubmitButton>Contact me</SubmitButton>
        </ContactCard>
      </ContactContainer>
      <ToastContainer />
    </PortfolioSection>
  );
};

export default Contacts;
